data.value:
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX yago: <http://dbpedia.org/class/yago/>
PREFIX dbo: <http://dbpedia.org/ontology/>

SELECT ?country ?countryLabel min(?thumbnail) as ?thumbnail
WHERE {{
    ?country rdf:type yago:EuropeanCountries .
    ?country rdf:type dbo:Country .
    ?country rdfs:label ?countryLabel.
OPTIONAL {{
    ?country dbo:thumbnail ?thumbnail
}}
FILTER(lang(?countryLabel)='en')
}}
ORDER BY ?countryLabel

PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>        
PREFIX dbo:  <http://dbpedia.org/ontology/> 
SELECT DISTINCT ?genreLabel
WHERE {{
?resource dbo:genre ?genre.
?resource rdfs:label '{0}'@en.
?genre rdfs:label ?genreLabel
FILTER(lang(?genreLabel) = 'en')
}}
LIMIT 100

PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>        
PREFIX dbo:  <http://dbpedia.org/ontology/> 
PREFIX schema:    <http://schema.org/>
SELECT DISTINCT ?song MIN(?title) AS ?title MIN(?thumbnail) AS ?thumbnail
WHERE {{
  ?song a dbo:Single .
  ?song dbp:title ?title .
  ?song dbp:artist ?artist .
  ?artist rdfs:label ?artistLabel .
OPTIONAL  {{
  ?song dbo:thumbnail ?thumbnail
}}
FILTER (CONTAINS(LCASE(STR(?title)), '{0}')) .
FILTER(CONTAINS(LCASE(STR(?artistLabel)), '{1}'))
}}
LIMIT 1

PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>        
PREFIX dbo:  <http://dbpedia.org/ontology/> 
PREFIX schema:    <http://schema.org/>
SELECT distinct ?artist ?artistName ?wiki ?homepage min(?thumbnail) as ?thumbnail ?abstract
WHERE {{
	VALUES ?type {{ schema:MusicGroup dbo:MusicalArtist }}
	VALUES ?website {{ dbp:website foaf:homepage }}
		?artist a ?type .
		?artist rdfs:label ?artistName.
		?artist dbo:abstract ?abstract .
		?artist dbo:thumbnail ?thumbnail .
		?artist foaf:isPrimaryTopicOf ?wiki . 
	OPTIONAL {{
		?artist ?website ?homepage .
		}}

  FILTER(lang(?artistName) = 'en') .
  FILTER(lang(?abstract) = 'en') .
FILTER(CONTAINS(LCASE(STR(?artistName)), '{0}')
    || CONTAINS(LCASE(STR(?wiki)), '{1}') || CONTAINS(LCASE(STR(?homepage)), '{2}')) .
}}
GROUP BY ?artist ?artistName ?wiki ?homepage ?thumbnail ?abstract
ORDER BY ?artistName
LIMIT 1

PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> 
PREFIX dbo:  <http://dbpedia.org/ontology/> 
PREFIX schema:    <http://schema.org/>

SELECT DISTINCT ?artist ?artistName  min(?thumbnail) as ?thumbnail
WHERE {{
 VALUES ?type {{ schema:MusicGroup dbo:MusicalArtist }}
 VALUES ?hometown {{ dbo:hometown dbo:birthplace }}
          ?artist a ?type ;
          rdfs:label ?artistName .
          ?artist ?hometown ?city.
  ?city dbo:country ?country.
  ?country rdfs:label '{0}'@en.
OPTIONAL {{
  ?artist dbo:thumbnail ?thumbnail
}}
  FILTER(lang(?artistName)='en')
}}
LIMIT 20
